#provides tracks for the hurricanes that occurred

library(dplyr)
library(tidyr)
storms_all &lt;- hurr_tracks %&gt;%
  select(storm_id) %&gt;%
  distinct() %&gt;%
  mutate(year = stringr::str_extract(storm_id, "-[0-9].+")) %&gt;%
  filter(year &lt;= 2018)
map_tracks((storms = storms_all$storm_id),
alpha = 0.5, plot_points = TRUE, color = "blue")







